import axios from "axios";
import React, { useState, useEffect } from "react";
import { Card } from "react-bootstrap";
import { Link } from "react-router-dom";

const ShowProducts = () => {
  const [products, setProducts] = useState([]);

  const getProducts = async () => {
    const response = await axios.get("http://localhost:8000/api/");
    setProducts(response.data);
  };

  useEffect(() => {
    getProducts();
  }, []);

  return (
    <div className="products-card-info">
      <h1>All Students</h1>
      {products.map((product, index) => (
        <div>
          <Card className="m-2 rounder shadow-lg" style={{ width: "22rem" }}>
            <Card.Img variant="top" src={product.image} />
            <Card.Body>
              <Card.Title>{product.name}</Card.Title>
              <Card.Text className="product-roll_no-info">
                {product.roll_no}
              </Card.Text>
              <Card.Text>{product.address}</Card.Text>
              <Card.Text className="product-standard-info">
                {product.standard}
              </Card.Text>
              <Link className="btn btn-primary" to={`/${product.id}/`}>
                Details
              </Link>
            </Card.Body>
          </Card>
        </div>
      ))}
    </div>
  );
};

export default ShowProducts;
