import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const UpdateProduct = () => {
  const [image, setImage] = useState(null);
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const [catogory, setCategory] = useState("");

  const navigate = useNavigate();
  const { id } = useParams();

  const loadProducts = async () => {
    const { data } = await axios.get(`http://localhost:8000/api/${id}/`);
    setImage(data.image);
    setName(data.name);
    setPrice(data.price);
    setCategory(data.catogory);
    setDescription(data.catogory);
  };

  useEffect(() => {
    loadProducts();
  }, []);

  //   Update Products

  const UpdateProductInfo = async () => {
    let formField = new FormData();
    formField.append("name", name);
    formField.append("price", price);
    formField.append("description", description);
    formField.append("catogory", catogory);
    if (image !== null) {
      formField.append("image", image);
    }

    await axios({
      method: "PUT",
      url: `http://localhost:8000/api/${id}/`,
      data: formField,
    }).then((responce) => {
      navigate("/");
    });
  };

  return (
    <div>
      <h1>Update product</h1>
      <div className="form-group">
        <div className="form-group">
          <img src={image} height="300" width="150" />
          <label>Select image to upload</label>
          <input
            type="file"
            className="form-control form-control-lg"
            name="image"
            onChange={(e) => setImage(e.target.files[0])}
          />
        </div>

        <div className="form-group">
          <input
            type="text"
            className="form-control form-control-lg"
            placeholder="Enter your Name"
            name="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>

        <div className="form-group">
          <input
            type="text"
            className="form-control form-control-lg"
            placeholder="Enter your Price"
            name="price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
        </div>
        <div className="form-group">
          <textarea
            className="form-control form-control-lg"
            placeholder="Enter your Description"
            name="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <div className="form-group">
          <input
            type="text"
            className="form-control form-control-lg"
            placeholder="Enter your Category"
            name="category"
            value={catogory}
            onChange={(e) => setCategory(e.target.value)}
          />
        </div>
        <button className="btn btn-success" onClick={UpdateProductInfo}>
          update Product
        </button>
      </div>
    </div>
  );
};
export default UpdateProduct;
