import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const UpdateProduct = () => {
  const [image, setImage] = useState(null);
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState(""); // Fixed typo

  const navigate = useNavigate();
  const { id } = useParams();

  const loadProducts = async () => {
    try {
      const { data } = await axios.get(`http://localhost:8000/api/${id}/`);
      setImage(data.image); // If `image` is a URL, display it
      setName(data.name);
      setPrice(data.price);
      setCategory(data.category);
      setDescription(data.description); // Fixed typo
    } catch (error) {
      console.error("Error loading product:", error);
    }
  };

  useEffect(() => {
    loadProducts();
  }, []);

  const UpdateProductInfo = async () => {
    let formField = new FormData();
    formField.append("name", name);
    formField.append("price", price);
    formField.append("description", description);
    formField.append("category", category);

    // Only append the image if it’s a new file
    if (image instanceof File) {
      formField.append("image", image);
    }

    try {
      await axios({
        method: "PUT",
        url: `http://localhost:8000/api/${id}/`,
        data: formField,
      });
      navigate("/");
    } catch (error) {
      console.error("Error updating product:", error.response || error);
    }
  };

  return (
    <div>
      <h1>Update product</h1>
      <div className="form-group">
        <div className="form-group">
          {image && !(image instanceof File) && (
            <img src={image} alt="Product" height="300" width="150" />
          )}
          <label>Select image to upload</label>
          <input
            type="file"
            className="form-control form-control-lg"
            name="image"
            onChange={(e) => setImage(e.target.files[0])}
          />
        </div>

        <div className="form-group">
          <input
            type="text"
            className="form-control form-control-lg"
            placeholder="Enter your Name"
            name="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>

        <div className="form-group">
          <input
            type="text"
            className="form-control form-control-lg"
            placeholder="Enter your Price"
            name="price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
        </div>
        <div className="form-group">
          <textarea
            className="form-control form-control-lg"
            placeholder="Enter your Description"
            name="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <div className="form-group">
          <input
            type="text"
            className="form-control form-control-lg"
            placeholder="Enter your Category"
            name="category"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          />
        </div>
        <button className="btn btn-success" onClick={UpdateProductInfo}>
          Update Product
        </button>
      </div>
    </div>
  );
};

export default UpdateProduct;
